// Extract and validate input
const body = $input.item.json.body || $input.item.json;
const phone_digits = body.phone_digits;
const message = body.message;
const from_number = body.from_number || '+15617867541';

if (!phone_digits || !message) {
  throw new Error('Missing required fields: phone_digits and message');
}

// Format phone to E.164
const digits = String(phone_digits).replace(/\D/g, '');
const to_e164 = digits.length === 10 ? `+1${digits}` : `+${digits}`;

return {
  to_e164,
  from_e164: from_number,
  message,
  phone_digits: digits
};
